PREFIX datacite: <http://purl.org/spar/datacite/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
SELECT ?scheme ?count (?count/?total * 100 AS ?percentage) WHERE {
  { SELECT ?scheme (COUNT(?s) AS ?count) WHERE {
      ?s rdf:type datacite:Identifier .
      ?s datacite:usesIdentifierScheme ?scheme .
      FILTER(
        ?scheme NOT IN (datacite:dblp, datacite:dblp-stream, datacite:dblp-record)
      )
    }
    GROUP BY ?scheme
  }
  { SELECT (COUNT(?s) AS ?total) WHERE {
      ?s rdf:type datacite:Identifier .
      ?s datacite:usesIdentifierScheme ?scheme .
      FILTER(
        ?scheme NOT IN (datacite:dblp, datacite:dblp-stream, datacite:dblp-record)
      )
    }
  }
}
ORDER BY DESC(?count)

PREFIX dblp: <https://dblp.org/rdf/schema#>
SELECT ?venue (COUNT(?paper) AS ?count) WHERE {
  ?paper dblp:publishedInStream ?venue .
}
GROUP BY ?venue
ORDER BY DESC(?count)

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dblp: <https://dblp.org/rdf/schema#>
SELECT ?author ?author_label ?count WHERE {
  { SELECT ?author ?author_label (COUNT(?paper) AS ?count) WHERE {
      ?paper dblp:authoredBy ?author .
      ?paper dblp:publishedInStream <https://dblp.org/streams/conf/sigir> .
      ?author rdfs:label ?author_label .
    }
    GROUP BY ?author ?author_label
  }
  FILTER REGEX(STR(?author_label), "M.*D.*", "i")
}
ORDER BY DESC(?count)

SELECT ?predicate (COUNT(*) AS ?count) WHERE {
  ?s ?predicate ?o .
}
GROUP BY ?predicate
ORDER BY DESC(?count)
