SELECT ?item ?name ?language_tag ?start ?point_of_time ?end
WITH
{
  # Select items with more than 3 official names, i.e. it has changed at least 3 times.
  SELECT ?item
  WHERE
  {
    ?item p:P1448 []. # Official name
  }
  GROUP BY ?item
  HAVING (COUNT(?item) > 3)
} AS %multiple_names
WITH
{
  SELECT ?item
  WHERE
  {
    INCLUDE %multiple_names
    ?item p:P1448 / ps:P1448 ?name.
  }
  GROUP BY ?item
  HAVING (COUNT(?item) > 3)
} AS %multiple_French_names
WHERE
{
  INCLUDE %multiple_French_names
  ?item wdt:P31 / wdt:P279 wd:Q486972. # ?item is a settelement
  ?item p:P1448 ?name_statement.
  ?name_statement ps:P1448 ?name.
  BIND (LANG(?name) AS ?language_tag).
  OPTIONAL { ?name_statement pq:P580 ?start. }
  OPTIONAL { ?name_statement pq:P585 ?point_of_time. }
  OPTIONAL { ?name_statement pq:P582 ?end. }
}
ORDER By ?item ?start ?point_of_time ?end

SELECT distinct ?item ?itemLabel ?countryLabel ?designation_countryLabel ?coords
with {
Select ?item  (count(?item) as ?count)          # this select gets us items with more than one country / heritage designation
               where {
  ?item wdt:P1435 ?designation.                 # item has a heritage designation
  ?item wdt:P17 ?country.                       # item has a country 
  ?designation wdt:P17 ?designation_country.    # the heritage designation has a country
  filter (?country!=?designation_country)       # the country of the heritage designation is not the listing country

} group by ?item  having (?count>1) } as %candidates
 where {
include %candidates                             # this select finds properties of the items found in the last select
  ?item wdt:P1435 ?designation.                 # item has a heritage designation
  ?item wdt:P17 ?country.                       # item has a country 
  ?designation wdt:P17 ?designation_country.    # the heritage designation has a country 
  OPTIONAL {?item wdt:P625 ?coords.}            # item may have coordinates
  filter (?country!=?designation_country)       # the country of the heritage designation is not the listing country
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
 } order by ?itemLabel ?countryLabel ?designationLabel

