SELECT ?EU_member ?population (COUNT(?found_larger_population) + 1 AS ?population_rank)
WITH
{
  SELECT ?EU_member ?population
  WHERE
  {
    wd:Q458 wdt:P150 ?EU_member .
    ?EU_member wdt:P1082 ?population .
  }
}
AS %get_members_and_population
WHERE
{
  INCLUDE %get_members_and_population
  {
    SELECT (?population AS ?population2)
    WHERE
    {
      INCLUDE %get_members_and_population
    }
  }
  BIND (IF(?population2 > ?population, true, 1/0) AS ?found_larger_population)
}
GROUP BY ?EU_member ?population
ORDER BY DESC(?population)

SELECT ?item ?instance ?year ?height ?length ?isle
with { select ?item ?isle WHERE {
  ?item wdt:P31/wdt:P279* wd:Q44377.
  ?item wdt:P17 wd:Q4628.
  OPTIONAL { ?item wdt:P5130 ?isle. }  
  } group by ?item ?isle } as %i
where 
{ include %i
  OPTIONAL { ?item wdt:P31 ?instance. }
  OPTIONAL { ?item wdt:P580 ?annee_debut_0. }
  OPTIONAL { ?item wdt:P571 ?annee_debut_1. }
  OPTIONAL { ?item wdt:P1619 ?annee_debut_2. }
  BIND(COALESCE(STR(YEAR(?annee_debut_2)), STR(YEAR(?annee_debut_1)), STR(YEAR(?annee_debut_0))) AS ?year)
  OPTIONAL { ?item wdt:P2048 ?height. }
  OPTIONAL { ?item wdt:P2043 ?length. }
}

SELECT ?partner_count (SAMPLE(?female_) AS ?female) (SAMPLE(?male_) AS ?male)

WITH {
  SELECT ?item ?p
WHERE 
{
 ?item wdt:P451 ?p.
 ?item wdt:P106/wdt:P279* wd:Q108289408 . 
} } as %t

WITH {
SELECT ?partner_count  (count(?partner_count) as ?female_) WHERE {  
  SELECT ?item ?g (count(?p) as ?partner_count) WHERE 
  {
    VALUES ?g {wd:Q6581072}
    INCLUDE %t
    ?item wdt:P21 ?g.  
  } group by ?item ?g } group by ?partner_count ?g } as %i

WITH {
SELECT ?partner_count  (count(?partner_count) as ?male_) WHERE {  
  SELECT ?item ?g (count(?p) as ?partner_count) 
  WHERE  
  {
    VALUES ?g {wd:Q6581097}
    INCLUDE %t
    ?item wdt:P21 ?g.  
  } group by ?item ?g } group by ?partner_count ?g } as %j

WHERE
{
  { INCLUDE %i }    
  UNION
  { INCLUDE %j }
} group by ?partner_count order by desc (?partner_count)

# Nombre d'unités légales par catégorie juridique de niveau 3 (500+)
SELECT ?catégorie ?nomfr ?count
WITH
{
  SELECT ?catégorie ?nomfr (COUNT(?item) AS ?count_1)
  WHERE
  {
    ?catégorie wdt:P31 wd:Q56427813; # catégorie juridique en France
        wdt:P361 wd:Q112176282; # catégorie de niveau 3
        wdt:P2561 ?nomfr. # nom de la catégorie
    FILTER(LANG(?nomfr) = "fr"). # nom en français
    ?item wdt:P1616 ?siren. # unité légale
    {  ?item p:P1454 [ps:P1454 ?catégorie; pq:P1013 wd:Q87711223; prov:wasDerivedFrom [ pr:P248 wd:Q3509449]; prov:wasDerivedFrom [ pr:P5017 ?date] ]. } # catégorie
    UNION
    { ?item p:P1454 [ps:P1454 ?catégorie; pq:P1013 wd:Q87711223; prov:wasDerivedFrom [ pr:P248 wd:Q3509449]; prov:wasDerivedFrom [a wdno:P5017] ]. } # catégorie
  }
  GROUP BY ?catégorie ?nomfr
} AS %all
WHERE
{
  {
    SELECT ?catégorie ?nomfr (?count_1 AS ?count) (?count as ?sortkey)
    WHERE
    {
      INCLUDE %all
      FILTER (?count_1 > 499) # minimum unités légales
    }
  }
  UNION
  {
    SELECT ("OTHER" AS ?catégorie) ("OTHER" AS ?nomfr) (SUM(?count_1) AS ?count) (0 AS ?sortkey)
    WHERE
    {
      INCLUDE %all
      FILTER (?count_1 <= 499)
    }    
  }
}
ORDER BY DESC(?sortkey)

SELECT DISTINCT ?person ?birthplace ?dob WITH {
  SELECT ?person ?birthplace ?dob WHERE {
  ?person wdt:P27 wd:Q30 ;
          wdt:P19/wdt:P131* wd:Q771 ;
          wdt:P19 ?birthplace ;
          wdt:P569 ?dob ;
  OPTIONAL {?person wdt:P570 ?dod .}
  MINUS {?person wdt:P570 [] .}
  FILTER (YEAR(?dob) > 1919)
} GROUP BY ?person ?birthplace ?dob
} AS %i WHERE {
    include %i 
    ?person wdt:P106/wdt:P279* wd:Q482980 .
} 
ORDER BY ASC (?dob)
