SELECT ?player ?playerLabel ?dob ?goal_date ?goal_age_days
WITH
{
  SELECT ?player ?dob ?date_first ?date_latest ?age_first ?age_latest
  WHERE
  {
    ?player p:P6509 ?p6509stm . # goals
    ?p6509stm pq:P7124 ?date_first .
    ?p6509stm pq:P7125 ?date_latest .
    ?p6509stm pq:P642 wd:Q484028 . # for UEFA Super Cup
    ?player wdt:P569 ?dob . # Date of birth
    BIND(?date_first - ?dob AS ?age_first)
    BIND(?date_latest - ?dob AS ?age_latest)
  }
} as %query
WHERE
{
  {
    SELECT ?player ?dob (?date_first AS ?goal_date) (?age_first AS ?goal_age_days)
    {
      INCLUDE %query
    }
    ORDER BY ASC(?goal_age_days)
    LIMIT 10
  }
  UNION
  {
    SELECT ?player ?dob (?date_latest AS ?goal_date) (?age_latest AS ?goal_age_days)
    {
      INCLUDE %query
    }
    ORDER BY DESC(?goal_age_days)
    LIMIT 10
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ASC(?goal_age_days)

SELECT ?item ?itemLabel (avg(?number) as ?passenger) (max(avg(?number) - ?number) as ?plus_minus) ?year 
with {
  select ?item ?statement ?year ?timevalue ?numberperperiod{
    ?item wdt:P238 ?IATA
    VALUES ?IATA {"JNB"}.
    ?item p:P3872 ?statement.
    ?statement pqv:P585 ?timevalue 
               ; ps:P3872 ?numberperperiod
               .
    ?timevalue wikibase:timeValue ?date .

    MINUS { ?statement wikibase:rank wikibase:DeprecatedRank }
    bind (YEAR(?date) AS ?year) 
    FILTER (?year >1985) .
  }
} as %airport

where {
  {
    select ?item ?year (sum(?numberperperiod) as ?number) {
      include %airport .
      ?timevalue wikibase:timePrecision ?prec filter ( ?prec > 9 )
    } group by ?item ?year
  } union {
      ?timevalue wikibase:timePrecision 9 .
    bind (?numberperperiod as ?number)
      include %airport
  } .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} group by ?item ?itemLabel ?year 
  order by ?item desc (?year)

SELECT (sum(?count) / 365 as ?count2) (min(?count) as ?mincount) (max(?count) as ?maxcount)
WITH {
  SELECT ?item ?date WHERE {
  ?item wdt:P31 wd:Q47150325. 
  ?item wdt:P585 ?date . hint:Prior hint:rangeSafe true.
  filter("2021-01-01"^^xsd:dateTime <= ?date && ?date < "2022-01-01"^^xsd:dateTime )
} } as %dates
WITH {
  SELECT ?date (count(?MP) as ?count) WHERE { 
  INCLUDE %dates
  ?MP p:P39 ?stat . 
  ?stat ps:P39 wd:Q77685926.
  filter not exists {?stat pq:P580 ?start . filter(?start > ?date ) }        
  filter not exists {?stat pq:P582 ?end . filter(?end < ?date ) }
  filter not exists {
    ?stat pq:P582 ?endt .   
    ?MP p:P39 ?stat2 . 
    ?stat2 ps:P39 wd:Q77685926.
    ?stat2 pq:P580 ?endt .
    filter(str(?stat2) != str(?stat))
    filter(?endt = ?date)
    }
} group by ?date } as %counts
WHERE
{
INCLUDE %counts

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". } 
} order by desc(?date)

SELECT ?sex ?sexLabel ?count
WITH
{
    SELECT ?sex (COUNT(?human) AS ?count)
    WHERE
    {
        ?human wdt:P31 wd:Q5.
        ?human wdt:P21 ?sex.
       VALUES ?sex {wd:Q48270 wd:Q1097630 wd:Q1052281 wd:Q2449503}.
    }
    GROUP BY ?sex
} AS %results
WHERE
{
    INCLUDE %results.
    SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

SELECT ?paper ?paperLabel ?count
WITH {
  SELECT ?paper WHERE {  
     ?paper wdt:P31 wd:Q13442814.
     ?paper p:P2093/pq:P1545 "1000" .
  }
} AS %papers 
WITH {
  SELECT ?paper (MAX(xsd:integer(?val)) AS ?count) WHERE {
     INCLUDE %papers .
     ?paper p:P2093/pq:P1545 ?val .
  } GROUP BY ?paper 
} AS %author_count
WHERE {
   INCLUDE %author_count .
   SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}                  
ORDER BY DESC(?count)

