SELECT ?item ?image ?label WITH {
  SELECT ?item (MAX(?p18) AS ?image) {
    ?item wdt:P31/wdt:P279* wd:Q31629 .
    OPTIONAL { ?item wdt:P18 ?p18 } .
  } GROUP BY ?item
} AS %subquery WHERE {
  INCLUDE %subquery .
  ?item rdfs:label ?label .
  FILTER ( LANG( ?label ) = "en" ) .
}

PREFIX schema: <http://schema.org/>
SELECT ?commune ?communeLabel ?communeImage ?communePostalCode ?communeWebsite

    WITH {
      SELECT ?commune ?communePostalCode WHERE {
        ?commune wdt:P31 wd:Q484170;
                 p:P625 ?coordinate.
        ?coordinate psv:P625 ?coordinate_node.
        ?commune wdt:P281 ?communePostalCode
        FILTER (?communePostalCode > "85000" && ?communePostalCode < "86000")
      }
      GROUP BY ?commune ?communePostalCode
    } AS %results 

    WHERE {
      INCLUDE %results.
      OPTIONAL { ?commune wdt:P18 ?communeImage }.
      OPTIONAL { ?commune wdt:P856 ?communeWebsite }.

      SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
    }

ORDER BY ?communePostalCode

# Egocentric co-author graph for an author
SELECT ?author1 ?author1Label ?rgb ?author2 ?author2Label
WITH {
  SELECT (COUNT(?work) AS ?count) ?author1 ?author2 WHERE {
    # Find co-authors
    ?work wdt:P50 wd:Q38382414, ?author1, ?author2 .

  }
  GROUP BY ?author1 ?author2
  ORDER BY DESC(?count)
} AS %authors
WITH {
  SELECT ?author1 ?author2 ?rgb WHERE {
    INCLUDE %authors

    # Exclude self-links
    FILTER (?author1 != ?author2)

    # Color according to gender
    OPTIONAL {
      ?author1 wdt:P21 ?gender1 . 
      BIND( IF(?gender1 = wd:Q6581097, "male", "other") AS ?rgb)
    }
  }
} AS %result
WHERE {
  INCLUDE %result
  # Label the results 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en,fr,de,ru,es,zh,jp". }
}
