PREFIX pq: <http://www.wikidata.org/prop/qualifier/>
SELECT DISTINCT ?senator ?senatorLabel WHERE {
  VALUES ?seat {
    wd:Q101498883 wd:Q101498993
  }
  ?senator p:P39 ?stmt .
  ?stmt ps:P39 wd:Q4416090 ; pq:P768 ?seat .
  OPTIONAL {
    ?stmt pq:P580 ?start .
  }
  OPTIONAL {
    ?stmt pq:P582 ?end .
  }
  FILTER ( ?start <= "2013-12-31T23:59:59Z" ^^ xsd:dateTime && ( ! BOUND( ?end ) || ?end >= "2013-01-01T00:00:00Z" ^^ xsd:dateTime ) ) OPTIONAL {
    ?senator rdfs:label ?senatorLabel FILTER ( LANG( ?senatorLabel ) = "en" )
  }
}

