{"kg":"wikidata","sparql":"SELECT ?vp ?vpLabel WHERE {
  # Find persons who held the office Vice President of the United States
  ?vp p:P39 ?stmt .
  ?stmt ps:P39 wd:Q13677;        # vice president position
        pq:P580 ?start;         # term start
        pq:P582 ?end .          # term end
  # Filter to those whose term overlapped Bill Clinton's presidency (1993-01-20 to 2001-01-20)
  FILTER(
    ?start <= "2001-01-20T00:00:00Z"^^xsd:dateTime &&
    ?end   >= "1993-01-20T00:00:00Z"^^xsd:dateTime
  )
  # Get English label for the vice president
  OPTIONAL {
    ?vp rdfs:label ?vpLabel .
    FILTER(LANG(?vpLabel) = "en")
  }
}","answer":"Al Gore (wd:Q57969)"}

